boilerplate.java:
import java.util.*;
class Inventory{
  Map<String,Integer> items=new HashMap<>();
  void addItem(String n,int q){ items.put(n,items.getOrDefault(n,0)+q); }
  void removeItem(String n,int q){ if(items.getOrDefault(n,0)>=q) items.put(n,items.get(n)-q); }
}
