calculator.boilerplate:
def add(a, b):
    return a + b

def subtract(a, b):
    return a - b

def multiply(a, b):
    return a * b

def divide(a, b):
    if b != 0:
        return a / b
    else:
        return 'Cannot divide by zero'

calculator.test:
def test_add():
    assert add(2, 3) == 5

def test_subtract():
    assert subtract(5, 2) == 3

def test_multiply():
    assert multiply(3, 4) == 12

def test_divide():
    assert divide(10, 2) == 5

rest_api.boilerplate:
from flask import Flask, jsonify, request
app = Flask(__name__)
@app.route('/api/data')
def get_data():
    return jsonify({'message': 'Hello from REST API!'})
@app.route('/api/sum', methods=['POST'])
def sum_numbers():
    data = request.get_json()
    return jsonify({'sum': data['a'] + data['b']})
if __name__ == '__main__':
    app.run(debug=True)

rest_api.test:
def test_api_get():
    assert True

def test_api_sum():
    assert True

