weather_app.boilerplate:
import requests
API_KEY='YOUR_KEY'
def get_weather(city):
    url=f'http://api.openweathermap.org/data/2.5/weather?q={city}&appid={API_KEY}'
    return requests.get(url).json()

weather_app.test:
def test_weather():
    data=get_weather('London')
    assert 'weather' in data

