file_manager.boilerplate:
import os

def list_files(path):
    return os.listdir(path)

def create_file(path, name):
    with open(os.path.join(path,name),'w') as f:
        f.write('')

file_manager.test:
def test_list():
    assert isinstance(list_files('.'), list)

def test_create():
    create_file('.','temp.txt')
    assert 'temp.txt' in list_files('.')

simple_chatbot.boilerplate:
def chat_response(msg):
    responses={'hi':'Hello!','bye':'Goodbye!'}
    return responses.get(msg,'I did not understand')

simple_chatbot.test:
def test_hi():
    assert chat_response('hi')=='Hello!'

def test_bye():
