banking_app.boilerplate:
accounts = {}

def deposit(acc, amount):
    accounts[acc]=accounts.get(acc,0)+amount
    return accounts[acc]

def withdraw(acc, amount):
    if accounts.get(acc,0)>=amount:
        accounts[acc]-=amount
        return accounts[acc]
    return 'Insufficient funds'

blog_app.boilerplate:
posts = []

def create_post(title,content):
    posts.append({'title':title,'content':content})
    return posts

def list_posts():
    return posts

blog_app.test:
def test_create():
    posts.clear()
    create_post('Hello','World')
    assert len(posts)==1

def test_list():
    assert len(list_posts())>=0

